export default function UzArea() {
    const x = useSpring(0, { stiffness: 500, damping: 40 });
    const y = useSpring(0, { stiffness: 500, damping: 40 });
    const opacity = useTransform(x, (value) => (value === 0 ? 0 : 1));
    
    const [hovered, setHovered] = useState({});
  
    const changeMouse = (e, region) => {
        const svg = e.currentTarget.closest("svg")
        const rect = svg.getBoundingClientRect()
    
        const xPos = e.clientX - rect.left + 10;
        const yPos = e.clientY - rect.top + 10;
    
        x.set(xPos);
        y.set(yPos);
      setHovered(region);
    };
  
    return <div className="relative">
        { hovered?.name && <>
            <motion.div
                className="bg-gray-800 pointer-events-none text-white text-sm px-2 py-1 rounded-md absolute z-20"
                style={{ x, y, opacity }}
                initial={{ scale: 0.8 }}
                animate={{ scale: 1 }}
                transition={{ type: "spring", stiffness: 600, damping: 30 }}
            >
                {hovered.name}
            </motion.div>
            <motion.span
                key={hovered.name}
                className="absolute z-10 origin-left"
                initial={{ scale: 0.8, opacity: 0 }}
                animate={{ scale: 1, opacity: 1 }}
                transition={{ type: "spring", stiffness: 500, damping: 30 }}
                style={{
                    left: `${hovered.cx + 3}px`,
                    top: `${hovered.cy + 6}px`,
                    pointerEvents: "none"
                }}
            >
                <div className="text-lg flex flex-col-reverse items-center px-3 pl-1.5 py-1 rounded-md -translate-x-1/2 -translate-y-full">
                    <svg xmlns="http://www.w3.org/2000/svg" width="28" height="28" viewBox="0 0 24 24" fill="green"><path d="M20 10c0 4.993-5.539 10.193-7.399 11.799a1 1 0 0 1-1.202 0C9.539 20.193 4 14.993 4 10a8 8 0 0 1 16 0"/><circle cx="12" cy="10" r="3" fill="white" /></svg>
                    {hovered.city}
                </div>
            </motion.span>
        </> }
        <svg
            baseProfile="tiny"
            height="652"
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth="2"
            className="stroke-bgcolor"
            width="1000"
            viewBox="0 0 1000 652"
            xmlns="http://www.w3.org/2000/svg"
        >
            <g>
                {Object.values(map).map((region, i) => {
                    return <g key={i}>
                        <path
                            d={region.path}
                            className="cursor-pointer fill-border hover:fill-primary transition"
                            onMouseMove={(e) => changeMouse(e, region)}
                            onMouseLeave={() => setHovered(null)}
                        />
                        <circle
                            strokeWidth={2}
                            r={5}
                            cx={region.cx}
                            cy={region.cy}
                            fill="red"
                            pointerEvents='none'
                        />
                    </g>
                })}
            </g>
        </svg>
    </div>
}